    public List<Review> toEntityListReviewsFromDTO(OrderDTO orderDTO, OrderDetails orderDetails) {
        List<Review> reviewList = new ArrayList<>();
        List<Bot> bots = findAllBotsMinusFilial(orderDTO, convertFilialDTOToFilial(orderDTO.getFilial()));
        String site = parfum;
        String category = orderDetails.getOrder().getCompany().getSubCategory().getSubCategoryTitle();
        int totalAmount = orderDTO.getAmount();

        long start = System.nanoTime();
        int timeoutMillis = 15_000;

        List<CompletableFuture<String>> futureReviews = new ArrayList<>();
        for (int i = 0; i < totalAmount; i++) {
            PromptDTO promptDTO = promptFactory.generatePrompt(site, category);
            futureReviews.add(reviewGenerationManager.generateReviewAsync(promptDTO));
        }

        List<String> texts = new ArrayList<>();
        for (CompletableFuture<String> future : futureReviews) {
            try {
                String text = future.get(timeoutMillis, TimeUnit.MILLISECONDS);
                if (text != null && !text.startsWith("‚ö†Ô∏è") && !texts.contains(text)) {
                    texts.add(text);
                }
                Thread.sleep(300);
            } catch (TimeoutException e) {
                log.warn("‚è± –û—Ç–∑—ã–≤ –Ω–µ —É—Å–ø–µ–ª —Å–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞—Ç—å—Å—è –∑–∞ {} –º—Å", timeoutMillis);
            } catch (Exception e) {
                log.error("‚ùå –û—à–∏–±–∫–∞ –ø–æ–ª—É—á–µ–Ω–∏—è —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞ –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏: {}", e.getMessage());
            }
        }

        long durationSec = (System.nanoTime() - start) / 1_000_000_000;
        log.info("üéØ –°–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞–Ω–æ {} –æ—Ç–∑—ã–≤–æ–≤ –∑–∞ {} —Å–µ–∫", texts.size(), durationSec);

        for (String text : texts) {
            Review review = toEntityReviewFromDTO(
                    orderDTO.getCompany(),
                    orderDetails,
                    orderDTO.getFilial(),
                    bots,
                    text
            );
            Review saved = reviewService.save(review);
            if (saved != null) {
                reviewList.add(saved);
            } else {
                log.warn("‚ö†Ô∏è –û—Ç–∑—ã–≤ –Ω–µ —Å–æ—Ö—Ä–∞–Ω—ë–Ω (–≤–æ–∑–º–æ–∂–Ω–æ, –¥—É–±–ª–∏–∫–∞—Ç): {}", text.length() > 80 ? text.substring(0, 80) + "..." : text);
            }
        }
        return reviewList;
    }





    public boolean changeReviewText(Long reviewId) {
        long start = System.nanoTime();

        Review review = reviewService.getReviewById(reviewId);
        if (review == null) {
            log.error("‚ùå Review —Å ID {} –Ω–µ –Ω–∞–π–¥–µ–Ω", reviewId);
            return false;
        }

        PromptDTO promptDTO = promptFactory.generatePrompt(
                "", // –∏–ª–∏ –ø–æ–¥—Å—Ç–∞–≤—å siteText, –µ—Å–ª–∏ —Ö–æ—á–µ—à—å –¥–∞–≤–∞—Ç—å –¥–∞–Ω–Ω—ã–µ –æ –∫–æ–º–ø–∞–Ω–∏–∏
                review.getSubCategory().getSubCategoryTitle()
        );

        String generatedText = null;
        int maxAttempts = 3;

        for (int i = 1; i <= maxAttempts; i++) {
            long attemptStart = System.nanoTime();
            try {
                log.info("üß† –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –æ—Ç–∑—ã–≤–∞ (–ø–æ–ø—ã—Ç–∫–∞ {})...", i);
                generatedText = reviewGeneratorService.safeGenerateSingleReview(promptDTO);

                if (generatedText == null || generatedText.isBlank()) {
                    log.warn("‚ö†Ô∏è –ü—É—Å—Ç–æ–π —Ä–µ–∑—É–ª—å—Ç–∞—Ç –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ ‚Äî –ø–æ–ø—ã—Ç–∫–∞ {}", i);
                    continue;
                }

                break;

            } catch (Exception e) {
                long durationMs = (System.nanoTime() - attemptStart) / 1_000_000;
                if (e.getCause() instanceof SocketTimeoutException) {
                    log.warn("‚è± Timeout –ø—Ä–∏ –ø–æ–ø—ã—Ç–∫–µ {} –∑–∞ {} –º—Å", i, durationMs);
                    try {
                        Thread.sleep(3000);
                    } catch (InterruptedException ie) {
                        Thread.currentThread().interrupt();
                        return false;
                    }
                } else {
                    log.error("‚ùå –û—à–∏–±–∫–∞ –ø—Ä–∏ –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ –æ—Ç–∑—ã–≤–∞ (–ø–æ–ø—ã—Ç–∫–∞ {}) –∑–∞ {} –º—Å", i, durationMs, e);
                }
            }
        }

        if (generatedText == null || generatedText.isBlank()) {
            log.error("‚ùå –ù–µ —É–¥–∞–ª–æ—Å—å —Å–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞—Ç—å –æ—Ç–∑—ã–≤ –ø–æ—Å–ª–µ {} –ø–æ–ø—ã—Ç–æ–∫", maxAttempts);
            return false;
        }

        review.setText(generatedText);
        boolean result = reviewService.save(review) != null;

        long end = System.nanoTime();
        log.info("‚úÖ –°–º–µ–Ω–∞ —Ç–µ–∫—Å—Ç–∞ –∑–∞–≤–µ—Ä—à–µ–Ω–∞ –∑–∞ {:.4f} —Å–µ–∫", (end - start) / 1_000_000_000.0);

        return result;
    }



    private Review toEntityReviewFromDTO(
            CompanyDTO companyDTO,
            OrderDetails orderDetails,
            FilialDTO filialDTO,
            List<Bot> bots,
            String textReview // <-- –ø–µ—Ä–µ–¥–∞—ë–º —É–∂–µ –≥–æ—Ç–æ–≤—ã–π —Ç–µ–∫—Å—Ç
    ) {
        var random = new SecureRandom();

        return Review.builder()
                .category(convertCategoryDTOToCompany(companyDTO.getCategoryCompany()))
                .subCategory(convertSubCompanyDTOToSubCompany(companyDTO.getSubCategory()))
                .text(textReview != null ? textReview : "–¢–µ–∫—Å—Ç –æ—Ç–∑—ã–≤–∞")
                .answer("")
                .orderDetails(orderDetails)
                .bot(!bots.isEmpty() ? bots.get(random.nextInt(bots.size())) : null)
                .filial(convertFilialDTOToFilial(filialDTO))
                .publish(false)
                .worker(orderDetails.getOrder().getWorker())
                .product(orderDetails.getProduct())
                .price(orderDetails.getProduct().getPrice())
                .build();
    }// –ö–æ–Ω–≤–µ—Ä—Ç–µ—Ä –∏–∑ DTO –¥–ª—è –æ—Ç–∑—ã–≤–∞




